const freqLabels = ["Never", "Sometimes", "Often", "Always"];

export default function GreenFlags() {
  const [checked, setChecked] = useState<Set<number>>(new Set());
  const [freq, setFreq] = useState<Record<number, number>>({});
  const [showResults, setShowResults] = useState(false);

  const toggleCheck = (i: number) => {
    const next = new Set(checked);
    next.has(i) ? next.delete(i) : next.add(i);
    setChecked(next);
    setShowResults(false);
  };

  const score = Math.round((checked.size / flags.length) * 100);

  const getVerdict = () => {
    if (checked.size >= 6) return { text: "Your relationship shows strong healthy patterns! ", color: "text-greenflag" };
    if (checked.size >= 4) return { text: "Solid foundation with room to grow", color: "text-success" };
    if (checked.size >= 2) return { text: "Some positives, but improvement needed", color: "text-warning" };
    return { text: "Consider if your needs are being met", color: "text-destructive" };
  };

  return (
    <div className="px-4 py-12 md:py-20">
      <div className="container mx-auto max-w-4xl">
        <div className="text-center">
          <div className="mx-auto flex h-14 w-14 items-center justify-center rounded-2xl bg-success/10">
            <Flag className="h-7 w-7 text-success" />
          </div>
          <h1 className="mt-6 text-3xl font-bold md:text-4xl">Green Flag Analyzer</h1>
          <p className="mt-3 text-muted-foreground">Celebrate what's working. Healthy relationships have these patterns.</p>
        </div>

        <div className="mt-10 space-y-4">
          {flags.map((f, i) => (
            <div key={i} className="rounded-2xl border border-border/50 bg-card p-5 shadow-card transition-all duration-300 hover:shadow-hover">
              <div className="flex items-start gap-4">
                <button
                  onClick={() => toggleCheck(i)}
                  className={`mt-0.5 flex h-6 w-6 flex-shrink-0 items-center justify-center rounded-md border-2 transition-colors ${
                    checked.has(i) ? "border-success bg-success" : "border-border"
                  }`}
                >
                  {checked.has(i) && <CheckCircle className="h-4 w-4 text-success-foreground" />}
                </button>
                <div className="flex-1">
                  <h3 className="font-semibold">{f.name}</h3>
                  <p className="mt-1 text-xs text-muted-foreground">{f.why}</p>
                  <ul className="mt-2 space-y-1">
                    {f.signs.map((s, j) => (
                      <li key={j} className="flex items-start gap-2 text-sm text-muted-foreground">
                        <span className="mt-1.5 h-1.5 w-1.5 rounded-full bg-success flex-shrink-0" />
                        {s}
                      </li>
                    ))}
                  </ul>
                  <div className="mt-3 rounded-lg bg-success/5 p-2.5">
                    <p className="text-xs italic text-muted-foreground">Example: {f.example}</p>
                  </div>
                  {checked.has(i) && (
                    <div className="mt-3 flex gap-2">
                      {freqLabels.map((label, fi) => (
                        <button
                          key={fi}
                          onClick={() => setFreq({ ...freq, [i]: fi })}
                          className={`rounded-full px-3 py-1 text-xs font-medium transition-colors ${
                            freq[i] === fi
                              ? "bg-success text-success-foreground"
                              : "bg-muted text-muted-foreground hover:bg-muted/80"
                          }`}
                        >
                          {label}
                        </button>
                      ))}
                    </div>
                  )}
                </div>
              </div>
            </div>
          ))}
        </div>

        {/* Results */}
        <div className="mt-8 rounded-2xl border border-border/50 bg-card p-6 shadow-hover text-center">
          <p className="text-sm text-muted-foreground">
            Green Flags Identified: <strong className="text-foreground">{checked.size}/{flags.length}</strong>
          </p>
          <div className="mx-auto mt-4 h-3 w-64 overflow-hidden rounded-full bg-muted">
            <div
              className="h-full rounded-full bg-success transition-all duration-500"
              style={{ width: `${score}%` }}
            />
          </div>
          <p className="mt-2 text-sm font-semibold text-gradient">{score}% Strength Score</p>

          <Button
            onClick={() => setShowResults(true)}
            className="mt-4 gradient-primary border-0 text-primary-foreground"
            disabled={checked.size === 0}
          >
            See Analysis
          </Button>

          {showResults && checked.size > 0 && (
            <div className="mt-6 animate-fade-in text-left">
              <p className={`text-center text-lg font-semibold ${getVerdict().color}`}>{getVerdict().text}</p>
              {checked.size < flags.length && (
                <div className="mt-4">
                  <p className="text-sm font-medium">Strengthen These Areas:</p>
                  <ul className="mt-2 space-y-1">
                    {flags.filter((_, i) => !checked.has(i)).map((f, i) => (
                      <li key={i} className="text-sm text-muted-foreground"> {f.name}</li>
                    ))}
                  </ul>
                </div>
              )}
              {checked.size > 0 && (
                <div className="mt-4">
                  <p className="text-sm font-medium">Keep Doing:</p>
                  <ul className="mt-2 space-y-1">
                    {flags.filter((_, i) => checked.has(i)).map((f, i) => (
                      <li key={i} className="text-sm text-success"> {f.name}</li>
                    ))}
                  </ul>
                </div>
              )}
            </div>
          )}
        </div>
      </div>
    </div>
  );
}

import { useState } from "react";
import { Button } from "@/components/ui/button";
import { Textarea } from "@/components/ui/textarea";
import { CheckCircle, Heart, Ear, Shield, ScanFace, Calendar, Thermometer, Gift, BookOpen, HeartHandshake, Handshake, Telescope, Bird, Star, Trophy, Flame } from "lucide-react";
import { useLanguage } from "@/contexts/LanguageContext";

const daysEn = [
  { icon: Heart, title: "Appreciation Foundation", task: "Share 3 things you appreciate about your partner", tip: "Be specific  focus on character, not just actions." },
  { icon: Ear, title: "Active Listening", task: "Have a 15-minute conversation where you ONLY listen", tip: "No interrupting. Summarize what you heard." },
  { icon: Shield, title: "Boundary Setting", task: 'Share one boundary you need respected', tip: 'Use "I need..." statements. Be clear and specific.' },
  { icon: ScanFace, title: "Accountability Moment", task: "Apologize for one thing you did wrong", tip: 'No "but" or excuses. Name the action and its impact.' },
