const daysHi = [
  { icon: Heart, title: "  ", task: "3             ", tip: "  -    ,    " },
  { icon: Ear, title: " ", task: "15         ", tip: "        " },
  { icon: Shield, title: " ", task: '        ', tip: '" ..."        ' },
  { icon: ScanFace, title: "  ", task: "      ", tip: ' ""          ' },
  { icon: Calendar, title: " ", task: " -     (  , 2+ )", tip: "       " },
  { icon: Thermometer, title: " -", task: "         (0-10)  ", tip: "      ,   " },
  { icon: Gift, title: "   ", task: "-       ", tip: ",  , ,   ,  " },
