export default function RepairPlan() {
  const { t, language } = useLanguage();
  const [completed, setCompleted] = useState<Set<number>>(new Set());
  const [notes, setNotes] = useState<Record<number, string>>({});
  const [expanded, setExpanded] = useState<number | null>(null);

  const days = language === 'hi' ? daysHi : daysEn;

  const toggleComplete = (i: number) => {
    const next = new Set(completed);
    next.has(i) ? next.delete(i) : next.add(i);
    setCompleted(next);
  };

  const streak = () => {
    let count = 0;
    for (let i = 0; i < days.length; i++) {
      if (completed.has(i)) count++;
      else break;
    }
    return count;
  };

  const progress = Math.round((completed.size / days.length) * 100);

  return (
    <div className="px-4 py-12 md:py-20">
      <div className="container mx-auto max-w-4xl">
        <div className="text-center">
          <h1 className="text-3xl font-bold md:text-4xl">{language === 'hi' ? "14-   " : "14-Day Relationship Repair Plan"}</h1>
          <p className="mt-3 text-muted-foreground">{language === 'hi' ? "       " : "Small daily actions that create lasting change"}</p>
        </div>

        {/* Stats */}
        <div className="mt-8 grid grid-cols-3 gap-4">
          <div className="rounded-2xl border border-border/50 bg-card p-4 text-center shadow-card">
            <div className="text-2xl font-bold text-gradient">{completed.size}/14</div>
            <p className="text-xs text-muted-foreground">{language === 'hi' ? "  " : "Days Complete"}</p>
          </div>
          <div className="rounded-2xl border border-border/50 bg-card p-4 text-center shadow-card">
            <div className="flex items-center justify-center gap-1 text-2xl font-bold">
              <Flame className="h-6 w-6 text-warning" /> {streak()}
            </div>
            <p className="text-xs text-muted-foreground">{language === 'hi' ? "  " : "Day Streak"}</p>
          </div>
          <div className="rounded-2xl border border-border/50 bg-card p-4 text-center shadow-card">
            <div className="text-2xl font-bold text-gradient">{progress}%</div>
            <p className="text-xs text-muted-foreground">{language === 'hi' ? "" : "Complete"}</p>
          </div>
        </div>

        {/* Progress bar */}
        <div className="mt-6 h-2 w-full overflow-hidden rounded-full bg-muted">
          <div className="h-full rounded-full gradient-primary transition-all duration-500" style={{ width: `${progress}%` }} />
        </div>

        {/* Day cards */}
        <div className="mt-8 space-y-3">
          {days.map((day, i) => {
            const Icon = day.icon;
            const isExpanded = expanded === i;
            const isDone = completed.has(i);

            return (
              <div
                key={i}
                className={`rounded-2xl border bg-card shadow-card overflow-hidden transition-all duration-300 ${isDone ? "border-success/30" : "border-border/50"
                  }`}
              >
                <button
                  onClick={() => setExpanded(isExpanded ? null : i)}
                  className="flex w-full items-center gap-4 p-5 text-left"
                >
                  <div className={`flex h-10 w-10 flex-shrink-0 items-center justify-center rounded-xl ${isDone ? "bg-success/10" : "bg-primary/10"
                    }`}>
                    {isDone ? (
                      <CheckCircle className="h-5 w-5 text-success" />
                    ) : (
                      <Icon className="h-5 w-5 text-primary" />
                    )}
                  </div>
                  <div className="flex-1 min-w-0">
                    <div className="flex items-center gap-2">
                      <span className="text-xs font-medium text-primary">{language === 'hi' ? ` ${i + 1}` : `Day ${i + 1}`}</span>
                      {isDone && <span className="text-xs text-success">{language === 'hi' ? " " : " Complete"}</span>}
                    </div>
                    <h3 className="font-semibold truncate">{day.title}</h3>
                  </div>
                </button>

                {isExpanded && (
                  <div className="border-t border-border/50 p-5 animate-fade-in">
                    <div className="rounded-xl bg-primary/5 p-4">
                      <p className="font-medium text-sm">{language === 'hi' ? "   " : " Today's Task"}</p>
                      <p className="mt-1 text-sm text-muted-foreground">{day.task}</p>
                    </div>
                    <div className="mt-3 rounded-xl bg-muted/50 p-3">
                      <p className="text-xs text-muted-foreground"> {day.tip}</p>
                    </div>
                    <div className="mt-4">
                      <label className="text-xs font-medium text-muted-foreground">{language === 'hi' ? " " : "Reflection Notes"}</label>
                      <Textarea
                        value={notes[i] || ""}
                        onChange={(e) => setNotes({ ...notes, [i]: e.target.value })}
                        placeholder={language === 'hi' ? "  ?   ?" : "How did this go? What did you learn?"}
                        className="mt-1 min-h-[80px] resize-none rounded-xl"
                      />
                    </div>
                    <Button
                      onClick={() => toggleComplete(i)}
                      className={`mt-4 w-full ${isDone ? "bg-muted text-muted-foreground hover:bg-muted/80" : "gradient-primary border-0 text-primary-foreground"}`}
                    >
                      {isDone
                        ? (language === 'hi' ? "  " : "Mark Incomplete")
                        : (language === 'hi' ? "   " : "Mark Complete ")}
                    </Button>
                  </div>
                )}
              </div>
            );
          })}
        </div>

        {completed.size === 14 && (
          <div className="mt-8 animate-fade-in rounded-2xl gradient-primary p-8 text-center text-primary-foreground">
            <Trophy className="mx-auto h-12 w-12" />
            <h2 className="mt-4 text-2xl font-bold">{language === 'hi' ? "  !" : " Congratulations!"}</h2>
            <p className="mt-2">{language === 'hi' ? " 14-         " : "You completed the 14-Day Repair Plan. That takes real commitment."}</p>
          </div>
        )}
      </div>
    </div>
  );
}

import { useState } from "react";
import { AlertTriangle, ChevronDown, ChevronUp, Shield } from "lucide-react";
import { Button } from "@/components/ui/button";

const patterns = [
  {
    name: "Gaslighting",
    signs: ['"You\'re too sensitive"', '"That never happened"', '"You\'re imagining things"'],
    harm: "Makes you doubt your reality and memory",
    severity: 5,
  },
  {
    name: "Silent Treatment",
    signs: ["Ignoring for days", "Refusing to communicate", "Withdrawing all affection as punishment"],
    harm: "Emotional manipulation and control tactic",
    severity: 4,
  },
  {
    name: "Manipulation",
    signs: ["Guilt-tripping", "Playing victim constantly", "Twisting your words"],
    harm: "Controls behavior through emotional coercion",
    severity: 5,
  },
  {
    name: "Excessive Jealousy",
    signs: ["Checking phone constantly", "Isolating from friends", "Accusing without reason"],
    harm: "Suffocates independence and trust",
    severity: 4,
  },
  {
    name: "Emotional Dependency",
    signs: ["Can't function without you", "Threatens self-harm if you leave", "Makes you responsible for their happiness"],
    harm: "Creates unhealthy codependency",
    severity: 3,
  },
  {
    name: "Controlling Behavior",
    signs: ["Dictates what you wear", "Controls finances entirely", "Monitors location constantly"],
    harm: "Removes autonomy and freedom",
    severity: 5,
  },
  {
    name: "Lack of Accountability",
    signs: ["Never apologizes", "Always your fault", "Deflects responsibility"],
    harm: "Prevents growth and resolution",
    severity: 3,
  },
  {
    name: "Love Bombing then Withdrawal",
    signs: ["Excessive affection at start", "Sudden coldness", "Creates addiction to highs"],
    harm: "Manipulation through intermittent reinforcement",
    severity: 4,
  },
];

export default function ToxicDetector() {
  const [selected, setSelected] = useState<Set<number>>(new Set());
  const [expanded, setExpanded] = useState<number | null>(null);
  const [showResults, setShowResults] = useState(false);

  const toggle = (i: number) => {
    const next = new Set(selected);
    next.has(i) ? next.delete(i) : next.add(i);
    setSelected(next);
    setShowResults(false);
  };

  const riskLevel = () => {
    const count = selected.size;
    if (count >= 5) return { label: "High Risk", color: "text-destructive", bg: "bg-destructive/10" };
    if (count >= 3) return { label: "Moderate Risk", color: "text-warning", bg: "bg-warning/10" };
    return { label: "Low Risk", color: "text-success", bg: "bg-success/10" };
  };

  return (
    <div className="px-4 py-12 md:py-20">
      <div className="container mx-auto max-w-4xl">
        <div className="text-center">
          <div className="mx-auto flex h-14 w-14 items-center justify-center rounded-2xl bg-destructive/10">
            <Shield className="h-7 w-7 text-destructive" />
          </div>
          <h1 className="mt-6 text-3xl font-bold md:text-4xl">Toxic Pattern Detector</h1>
          <p className="mt-3 text-muted-foreground">Identify unhealthy behaviors before they cause lasting harm</p>
        </div>

        <div className="mt-4 rounded-xl border border-warning/30 bg-warning/5 p-4 text-center text-sm text-warning">
          <AlertTriangle className="mr-1 inline h-4 w-4" />
          This tool helps identify patterns. For severe abuse, contact professional help.
        </div>

        <div className="mt-8 space-y-3">
          {patterns.map((p, i) => (
            <div key={i} className="rounded-2xl border border-border/50 bg-card shadow-card overflow-hidden">
              <button
                onClick={() => setExpanded(expanded === i ? null : i)}
                className="flex w-full items-center justify-between p-5 text-left"
              >
                <div className="flex items-center gap-4">
                  <input
                    type="checkbox"
                    checked={selected.has(i)}
                    onChange={() => toggle(i)}
                    onClick={(e) => e.stopPropagation()}
                    className="h-5 w-5 rounded border-border text-primary accent-primary"
                  />
                  <div>
                    <h3 className="font-semibold">{p.name}</h3>
                    <div className="mt-1 flex gap-0.5">
                      {[...Array(5)].map((_, j) => (
                        <span key={j} className={j < p.severity ? "text-destructive" : "text-muted-foreground/30"}>
                          
                        </span>
                      ))}
                    </div>
                  </div>
                </div>
                {expanded === i ? <ChevronUp className="h-5 w-5 text-muted-foreground" /> : <ChevronDown className="h-5 w-5 text-muted-foreground" />}
              </button>
              {expanded === i && (
                <div className="border-t border-border/50 p-5 animate-fade-in">
                  <p className="text-sm font-medium text-muted-foreground">What it looks like:</p>
                  <ul className="mt-2 space-y-1">
                    {p.signs.map((s, j) => (
                      <li key={j} className="flex items-start gap-2 text-sm">
                        <span className="mt-1 h-1.5 w-1.5 rounded-full bg-destructive flex-shrink-0" />
                        {s}
                      </li>
                    ))}
                  </ul>
                  <div className="mt-4 rounded-xl bg-destructive/5 p-3">
                    <p className="text-sm"><strong>Why it's harmful:</strong> {p.harm}</p>
                  </div>
                </div>
              )}
            </div>
          ))}
        </div>

        {/* Results */}
        <div className="mt-8 sticky bottom-4 rounded-2xl border border-border/50 bg-card p-5 shadow-hover">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-sm text-muted-foreground">Patterns Selected: <strong>{selected.size}</strong></p>
              {selected.size > 0 && (
                <p className={`text-sm font-semibold ${riskLevel().color}`}>
                  Overall: {riskLevel().label}
                </p>
              )}
            </div>
            <Button
              onClick={() => setShowResults(true)}
              disabled={selected.size === 0}
              className="gradient-primary border-0 text-primary-foreground"
            >
              Get Analysis
            </Button>
          </div>
          {showResults && selected.size > 0 && (
            <div className="mt-4 animate-fade-in border-t border-border/50 pt-4">
              <div className={`rounded-xl p-4 ${riskLevel().bg}`}>
                <p className={`font-semibold ${riskLevel().color}`}>
                  {selected.size >= 5
                    ? " This relationship shows signs of significant unhealthy dynamics."
                    : selected.size >= 3
                    ? " Several concerning patterns detected. Consider setting boundaries and seeking counseling."
                    : " Some patterns to watch for. Open communication can help address these early."}
                </p>
              </div>
              <div className="mt-4 space-y-2">
                <p className="text-sm font-medium">Recommended Actions:</p>
                <ul className="space-y-1 text-sm text-muted-foreground">
                  <li> Set clear boundaries with your partner</li>
                  <li> Consider professional counseling</li>
                  <li> Talk to trusted friends or family</li>
                  {selected.size >= 5 && <li> Consider creating a safety plan</li>}
                </ul>
              </div>
            </div>
          )}
        </div>
      </div>
    </div>
  );
}
